import './index.scss'
import React, {useState} from 'react'

const TextArea = () => {
  const [count, setCount] = useState(0)

  return (
    <div>
      <textarea
        onChange={e => setCount(e.target.value.length)}
      />
    </div>
  )
}

export default TextArea
